public class RunPitaron {    public static int[] arr = new int[3];public static int[] SelectionSort(int[] array) { for (int i = 0; i < 3; i++) {arr[i] = 0; }for(int v=0;v<10;v++){        arr[0]++;        int n = array.length;        for (int i = 0; i < n - 1; i++) {        arr[1]++;            for (int j = 0; j < n - i - 1; j++)            {        arr[2]++;                if (array[j] > array[j + 1]) {                    int temp = array[j];                    array[j] = array[j + 1];                    array[j + 1] = temp;                }            }        }}       return array;    }}
